mapper.select:
select worker_id as 'workerId', year, month, check_times as 'checkTimes', del_flag as 'delFlag' from checkInRecord
and worker_id = #{workerId}
and year = #{year}
and month = #{month}
limit 1

mapper.update:
update checkInRecord set
check_times,
where id = #{id}

update checkInRecord set
del_flag = '1'
where id = #{id}
