mapper.select:
select
member_id as 'memberId',
consume_total as 'consumeTotal',
consume_discount as 'consumeDiscount',
consume_date as 'consumeDate',
del_flag as 'delFlag'
from worker

    
        and username= #{username}
    
    
        and password = #{password}
    
    
        and gender = #{gender}
    
    
        and worker_type = #{workerType}
    

limit 1

mapper.update:
update worker set
del_flag = '1'
where id = #{id}
